const idiomas={pt:[{frase:"A vida é uma aventura ousada ou nada.",autor:"Helen Keller"},{frase:"O sucesso nasce do querer, da determinação e persistência.",autor:"Júlio César"},{frase:"Seja a mudança que você quer ver no mundo.",autor:"Mahatma Gandhi"},{frase:"Grandes conquistas começam com pequenos passos.",autor:"Lao Tzu"},{frase:"O futuro pertence àqueles que acreditam na beleza dos seus sonhos.",autor:"Eleanor Roosevelt"}],en:[{frase:"Life is either a daring adventure or nothing.",autor:"Helen Keller"},{frase:"Success is born from desire, determination, and persistence.",autor:"Julius Caesar"},{frase:"Be the change that you want to see in the world.",autor:"Mahatma Gandhi"},{frase:"Great achievements start with small steps.",autor:"Lao Tzu"},{frase:"The future belongs to those who believe in the beauty of their dreams.",autor:"Eleanor Roosevelt"}],es:[{frase:"La vida es una aventura atrevida o nada.",autor:"Helen Keller"},{frase:"El éxito nace del deseo, la determinación y la persistencia.",autor:"Julio César"},{frase:"Sé el cambio que deseas ver en el mundo.",autor:"Mahatma Gandhi"},{frase:"Grandes logros comienzan con pequeños pasos.",autor:"Lao Tzu"},{frase:"El futuro pertenece a aquellos que creen en la belleza de sus sueños.",autor:"Eleanor Roosevelt"}],fr:[{frase:"La vie est soit une aventure audacieuse, soit rien.",autor:"Helen Keller"},{frase:"Le succès naît du désir, de la détermination et de la persévérance.",autor:"Jules César"},{frase:"Soyez le changement que vous voulez voir dans le monde.",autor:"Mahatma Gandhi"},{frase:"Les grandes réalisations commencent par de petits pas.",autor:"Lao Tzu"},{frase:"L'avenir appartient à ceux qui croient en la beauté de leurs rêves.",autor:"Eleanor Roosevelt"}],de:[{frase:"Das Leben ist entweder ein gewagtes Abenteuer oder nichts.",autor:"Helen Keller"},{frase:"Erfolg entsteht aus dem Wunsch, der Entschlossenheit und der Ausdauer.",autor:"Julius Caesar"}],it:[{frase:"La vita è un'avventura audace o niente.",autor:"Helen Keller"},{frase:"Il successo nasce dal desiderio, dalla determinazione e dalla perseveranza.",autor:"Giulio Cesare"}]};let idiomaAtual="pt";function gerarFrase(){const e=idiomas[idiomaAtual][Math.floor(Math.random()*idiomas[idiomaAtual].length)];document.getElementById("frase").textContent=`"${e.frase}" - ${e.autor}`}function alternarIdioma(){const e=["pt","en","es","fr","de","it"],a=e.indexOf(idiomaAtual);idiomaAtual=e[(a+1)%e.length];const t=idiomas[idiomaAtual][Math.floor(Math.random()*idiomas[idiomaAtual].length)],s=document.getElementById("frase");s.textContent=t.frase,s.className=idiomaAtual}function lerFrase(){const e=document.getElementById("frase").textContent,a=new SpeechSynthesisUtterance(e);a.lang="pt"===idiomaAtual?"pt-BR":"en"===idiomaAtual?"en-US":"es"===idiomaAtual?"es-ES":"fr"===idiomaAtual?"fr-FR":"de"===idiomaAtual?"de-DE":"it-IT",a.rate=1,a.pitch=1,a.volume=1,speechSynthesis.speak(a)}